INSERT INTO login_bonus_message_data VALUES(17,60000,2,0,4,2,0,'','오늘의 운세는 소길! 신경 쓰지 않고 마음을 편히 가지면 행운 상승! 행운의 인물은 아이돌. 노래가 당신을 행복하게 해줄 거예요♪',0);
