INSERT INTO sd_navi_comment VALUES(1000001,1000,190801,13,'다양한 미니 게임이\n준비되어 있습니다!\n어떤 미니 게임을 해 보시겠어요?',0,'2020-02-15 15:00:00','2030-04-01 14:00:00');
INSERT INTO sd_navi_comment VALUES(1000002,1000,190801,13,'즐거운 미니 게임 소개!\n당신은 어떤 미니 게임을\n하실 건가요?',0,'2020-02-15 15:00:00','2030-04-01 14:00:00');
INSERT INTO sd_navi_comment VALUES(1001001,1001,190801,13,'이건 {0}루피로 판매 중!\n꼭 구입해서 즐겨 보세요!',0,'2020-02-15 15:00:00','2030-04-01 14:00:00');
INSERT INTO sd_navi_comment VALUES(1002001,1002,190801,12,'움직이는 하트에 맞춰 터치!\n시즈루 씨와 에리코 씨의\n초콜릿 케이크 만들기를\n도와주세요!',0,'2020-02-15 15:00:00','2030-04-01 14:00:00');
INSERT INTO sd_navi_comment VALUES(1003001,1003,190801,11,'쿠우카 씨를 당겨서\n더 멀리 날려 주세요!\n다양한 해프닝으로 쿠우카 씨에게\n엑스터시를 선사하세요!',0,'2020-02-15 15:00:00','2030-04-01 14:00:00');
INSERT INTO sd_navi_comment VALUES(1004001,1004,190801,3,'좌우를 터치하여 마지문을\n쓰러뜨리세요!\n발사! 류큐견 어라운드 슛!',0,'2020-02-15 15:00:00','2030-04-01 14:00:00');
INSERT INTO sd_navi_comment VALUES(1005001,1005,190801,7,'패널을 터치하여 드래곤즈 네스트 일행과\n함께 끝말잇기 대결을 해보지 않으시겠어요?\n끝말에 「한방 단어」는 금지예요',0,'2030-02-15 15:00:00','2030-04-01 14:00:00');
